const html = $input.first().json.html || $input.first().json.body || $input.first().json.data || '';
const url = $input.first().json.url || '';

if (!html) {
  return [{ json: { error: 'No HTML input', url } }];
}

// ---------- TITLE ----------
let title = '';
const headings = html.match(/<h[1-2][^>]*>([\s\S]*?)<\/h[1-2]>/gi) || [];

for (const h of headings) {
  const clean = h.replace(/<[^>]*>/g, '').trim();
  if (
    clean.length > 20 &&
    clean.length < 120 &&
    !/home|menu|login|sign up|category/i.test(clean)
  ) {
    title = clean;
    break;
  }
}

// Fallback to <title>
if (!title) {
  const t = html.match(/<title[^>]*>(.*?)<\/title>/i);
  if (t) title = t[1].replace(/\s+/g, ' ').trim();
}

// ---------- MAIN CONTENT ----------
let content = '';

// Remove junk blocks first
const cleanedHtml = html
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '')
  .replace(/<nav[\s\S]*?<\/nav>/gi, '')
  .replace(/<footer[\s\S]*?<\/footer>/gi, '')
  .replace(/<header[\s\S]*?<\/header>/gi, '')
  .replace(/<!--[\s\S]*?-->/g, '');

// Grab paragraphs
const paragraphs = cleanedHtml.match(/<p\b[^>]*>(.*?)<\/p>/gi) || [];

for (const p of paragraphs) {
  let text = p
    .replace(/<a[^>]*>(.*?)<\/a>/gi, '$1')
    .replace(/<[^>]*>/g, ' ')
    .replace(/&nbsp;|&#160;/gi, ' ')
    .replace(/&amp;/gi, '&')
    .replace(/\s+/g, ' ')
    .trim();

  // ‚ùå FILTER OUT JUNK
  const junkPatterns = [
  /cookie/i,
  /privacy policy/i,
  /terms of service/i,
  /accept/i,
  /subscribe/i,
  /newsletter/i,
  /sign up/i,
  /log in/i,
  /related articles/i,
  /more stories/i,
  /follow us/i,
  /advertisement/i,
  /¬©/,
  /all rights reserved/i
];

const isJunk = junkPatterns.some(r => r.test(text));

if (
  text.length > 120 &&
  !/^https?:\/\//i.test(text) &&
  !/^\d{4}-\d{2}-\d{2}$/.test(text) &&
  !isJunk
) {
  content += text + '\n\n';
}
}

// Fallback: extract meaningful sentences if paragraphs failed
if (content.length < 300) {
  const plain = cleanedHtml
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  const sentences = plain.match(/[A-Z][^.!?]{60,}[.!?]/g) || [];
  content = sentences.slice(0, 25).join('\n\n');
}

// FINAL CLEAN
content = content
  .replace(/\b\d{4}-\d{2}-\d{2}\b/g, '')   // remove dates
  .replace(/https?:\/\/\S+/g, '')         // remove URLs
  .replace(/\s+\n/g, '\n')
  .trim()
  .substring(0, 15000);

// ---------- OUTPUT ONLY WHAT MATTERS ----------
return [{
  json: {
    title,
    content,
    url
  }
}];

// Simple HTML to Google Drive converter with link preservation
const boundary = 'divider';
const docName = $input.first().json.document_name;
const folderId = $input.first().json.drive_folder_id;
const htmlContent = $input.first().json.html_content;

// Process HTML to ensure links are preserved
function preserveLinks(htmlContent) {
    // Ensure all links have proper attributes for Google Drive
    let processedHtml = htmlContent;

    // Fix any malformed or incomplete link tags
    processedHtml = processedHtml.replace(/<a\s+href=["']([^"']+)["'][^>]*>(.*?)<\/a>/gi, '<a href="$1">$2</a>');

    // Ensure links have proper protocol
    processedHtml = processedHtml.replace(/<a\s+href=["'](?!https?:\/\/|mailto:|tel:)([^"']+)["']>/gi, '<a href="https://$1">');

    return processedHtml;
}

const metadata = JSON.stringify({
  name: docName,
  mimeType: "application/vnd.google-apps.document",
  parents: [folderId]
});

// Process the HTML content to preserve links
const processedHtmlContent = preserveLinks(htmlContent);

const htmlWithStyles = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    /* Add bottom margin to block elements for spacing */
    p,
    ul,
    ol,
    table,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
      margin-bottom: 10pt;
    }
    h2 {
      margin-top: 20pt;
    }
    /* Prevent margin collapse issues or excessive space inside lists */
    li {
       margin-bottom: 2pt; /* Optional: small space between list items */
    }
    /* Ensure links are styled properly for Google Drive */
    a {
      color: #1155cc;
      text-decoration: underline;
    }
    /* Remove margin from the last child within common containers if needed */
    /* This might be overly aggressive, test without it first */
    /*
    body > *:last-child,
    li > *:last-child {
       margin-bottom: 0;
    }
    */
  </style>
</head>
<body>
  ${processedHtmlContent}
</body>
</html>
`;

// Construct the body with literal \r\n ONLY
let body = `--${boundary}\r\n`;
body += `Content-Type: application/json; charset=UTF-8\r\n`;
body += `\r\n`; // Blank line
body += `${metadata}\r\n`;
body += `--${boundary}\r\n`;
body += `Content-Type: text/html\r\n`;
body += `\r\n`; // Blank line
body += `${htmlWithStyles}\r\n`; // Add the HTML content
body += `--${boundary}--\r\n`; // Final boundary

return {
  rawData: body 
};

const blocked = ["youtube.com", "facebook.com", "twitter.com", "instagram.com", "reddit.com"];

const urls = $json.organic
  .filter(r => !blocked.some(b => r.link.includes(b)))
  .slice(0, 5)
  .map(r => ({
    url: r.link,
    title: r.title,
    snippet: r.snippet
  }));

return urls;

const items = $input.all();

const found = items.find(item => item.json.keyword);

return [
  {
    json: {
      keyword: found ? found.json.keyword : null
    }
  }
];
