// Get HTML and DEBUG what's actually there
const html = $input.first().json.html || $input.first().json.body || $input.first().json.data || '';
const url = $input.first().json.url || '';

if (!html || html.length === 0) {
  return [{ json: { error: 'No HTML input', url } }];
}

const article = {
  title: '',
  content: '',
  author: '',
  date: '',
  imageUrl: '',
  url,
  debug: {
    htmlLength: html.length,
    hasParagraphs: html.includes('<p>'),
    hasDivs: html.includes('<div'),
    hasArticle: html.includes('<article')
  }
};

// ========== 1. FORCE TITLE EXTRACTION ==========
const allHeadings = html.match(/<h[1-6][^>]*>([\s\S]*?)<\/h[1-6]>/gi) || [];
const titleCandidates = allHeadings.map(h => {
  return (h.replace(/<[^>]*>/g, '').match(/[A-Z][a-zA-Z\s\-,]{10,100}/) || [''])[0];
}).filter(t => t && t.length > 15 && !t.includes('Categories') && !t.includes('About'));

article.title = titleCandidates[0] || '';

// ========== 2. NUCLEAR CONTENT EXTRACTION ==========
// Strategy 1: Find ALL text between <p> tags
let content = '';
const paragraphs = html.match(/<p\b[^<]*(?:(?!<\/p\b)[^<])*([^<]*(?:&[^;\s]*;[^<]*)*)(?!<\/p\b)[^<]*<\/p>/gi) || [];

paragraphs.forEach(p => {
  let text = p
    .replace(/<a[^>]*>([^<]*)<\/a>/gi, '$1')  // Keep link text
    .replace(/<[^>]*>/g, ' ')                 // Remove all tags
    .replace(/&nbsp;|&#160;/gi, ' ')
    .replace(/&#8217;|&#x27;/gi, "'")
    .replace(/&#8220;|&#8221;|"/gi, '"')
    .replace(/&amp;/gi, '&')
    .replace(/\s+/g, ' ')
    .trim();

  if (text.length > 30 && text.search(/[A-Za-z]{4,}/) > -1) {
    content += text + '\n\n';
  }
});

// Strategy 2: If no paragraphs, grab ALL text content
if (content.length < 100) {
  content = html
    .replace(/<script\b[\s\S]*?<\/script>/gi, '')     // Remove scripts
    .replace(/<style\b[\s\S]*?<\/style>/gi, '')      // Remove styles
    .replace(/<!--[\s\S]*?-->/g, '')                // Remove comments
    .replace(/<[^>]*>/g, ' ')                       // Remove ALL tags
    .replace(/\s+/g, ' ')
    .trim();

  // Extract sentences (heuristic for real content)
  const sentences = content.match(/[A-Z][^.!?]{50,}[.!?]+/g) || [];
  content = sentences.slice(0, 20).join('\n\n');
}

// Strategy 3: FINAL fallback - grab ANY substantial text block
if (content.length < 200) {
  const textBlocks = html.match(/[a-zA-Z\s\-,]{100,1000}[.!?;]/g) || [];
  content = textBlocks.slice(0, 15).join('\n\n');
}

article.content = content.substring(0, 10000);

// ========== 3. FORCE IMAGE ==========
const imgMatch = html.match(/https?:\/\/[^"\s<>]+\.(?:png|jpg|jpeg|webp|gif)(?:\?[^"\s<>]*)?(?=[ "\s<>])/i);
article.imageUrl = imgMatch ? imgMatch[0] : article.imageUrl;

// ========== 4. FORCE AUTHOR ==========
const authorMatch = html.match(/(?:by|author|writer)[:\s]*([A-Z][a-zA-Z\s\-\.]{2,50})/i);
article.author = authorMatch ? authorMatch[1].trim() : '';

// ========== 5. FORCE DATE ==========
const dateMatch = html.match(/\d{4}-\d{2}-\d{2}|\w+\s+\d{1,2},\s+\d{4}/);
article.date = dateMatch ? dateMatch[0] : article.date;

article.debug.contentLength = article.content.length;
article.debug.paragraphsFound = paragraphs.length;

return [{ json: article }];

// Simple HTML to Google Drive converter with link preservation
const boundary = 'divider';
const docName = $input.first().json.document_name;
const folderId = $input.first().json.drive_folder_id;
const htmlContent = $input.first().json.html_content;

// Process HTML to ensure links are preserved
function preserveLinks(htmlContent) {
    // Ensure all links have proper attributes for Google Drive
    let processedHtml = htmlContent;

    // Fix any malformed or incomplete link tags
    processedHtml = processedHtml.replace(/<a\s+href=["']([^"']+)["'][^>]*>(.*?)<\/a>/gi, '<a href="$1">$2</a>');

    // Ensure links have proper protocol
    processedHtml = processedHtml.replace(/<a\s+href=["'](?!https?:\/\/|mailto:|tel:)([^"']+)["']>/gi, '<a href="https://$1">');

    return processedHtml;
}

const metadata = JSON.stringify({
  name: docName,
  mimeType: "application/vnd.google-apps.document",
  parents: [folderId]
});

// Process the HTML content to preserve links
const processedHtmlContent = preserveLinks(htmlContent);

const htmlWithStyles = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    /* Add bottom margin to block elements for spacing */
    p,
    ul,
    ol,
    table,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6 {
      margin-bottom: 10pt;
    }
    h2 {
      margin-top: 20pt;
    }
    /* Prevent margin collapse issues or excessive space inside lists */
    li {
       margin-bottom: 2pt; /* Optional: small space between list items */
    }
    /* Ensure links are styled properly for Google Drive */
    a {
      color: #1155cc;
      text-decoration: underline;
    }
    /* Remove margin from the last child within common containers if needed */
    /* This might be overly aggressive, test without it first */
    /*
    body > *:last-child,
    li > *:last-child {
       margin-bottom: 0;
    }
    */
  </style>
</head>
<body>
  ${processedHtmlContent}
</body>
</html>
`;

// Construct the body with literal \r\n ONLY
let body = `--${boundary}\r\n`;
body += `Content-Type: application/json; charset=UTF-8\r\n`;
body += `\r\n`; // Blank line
body += `${metadata}\r\n`;
body += `--${boundary}\r\n`;
body += `Content-Type: text/html\r\n`;
body += `\r\n`; // Blank line
body += `${htmlWithStyles}\r\n`; // Add the HTML content
body += `--${boundary}--\r\n`; // Final boundary

return {
  rawData: body 
};

const blocked = ["youtube.com", "facebook.com", "twitter.com", "instagram.com", "reddit.com"];

const results = $json.organic;

const urls = results
  .map(r => ({
    url: r.link,
    title: r.title,
    snippet: r.snippet
  }))
  .filter(r => !blocked.some(b => r.url.includes(b)))
  .slice(0, 5);

// Create an array of objects with url1, title1, snippet1, etc.
const formattedUrls = urls.map((u, index) => ({
  [`url${index + 1}`]: u.url,
  [`title${index + 1}`]: u.title,
  [`snippet${index + 1}`]: u.snippet
}));

// Return the formatted array
return formattedUrls;

const items = $input.all();

const found = items.find(item => item.json.keyword);

return [
  {
    json: {
      keyword: found ? found.json.keyword : null
    }
  }
];
