const items = $input.all();

let taskText = "";

items.forEach((item, index) => {
  const task = item.json;

  taskText += `Task ${index + 1}\\n`;
  taskText += `Task Name: ${task.task_name || "N/A"}\\n`;
  taskText += `Description: ${task.description || "N/A"}\\n`;

  if (Array.isArray(task.comments) && task.comments.length) {
    taskText += `Comments:\\n`;
    task.comments.forEach(comment => {
      taskText += `- ${comment}\\n`;
    });
  }

  taskText += `\\n----------------------\\n\\n`;
});

// Escape quotes too (VERY important)
taskText = taskText.replace(/"/g, '\\"');

return [
  {
    json: {
      formattedTasks: taskText
    }
  }
];

// Get the array inside "output"
const tasks = $json.output;

if (!Array.isArray(tasks)) {
  throw new Error("Expected 'output' to be an array of tasks");
}

return tasks.map(task => {
  return {
    json: {
      task_name: task.task_name || "",
      objective: task.objective || "",
      actions: task.actions || [],
      constraints_and_considerations: task.constraints_and_considerations || [],
      dependencies_or_inputs: task.dependencies_or_inputs || [],
      validation_and_testing: task.validation_and_testing || []
    }
  };
});
