const data = $input.first().json;

// Transcript array
const transcript = data.transcript;

if (!transcript || transcript.length === 0) {
  throw new Error("Transcript is empty");
}

let conversation = "";
let lastSpeaker = null;

for (const line of transcript) {
  const speaker = line.speaker?.display_name || "Unknown";
  const text = line.text?.trim();

  if (!text) continue;

  // If speaker changes, start a new paragraph
  if (speaker !== lastSpeaker) {
    conversation += `\n\n${speaker}: ${text}`;
    lastSpeaker = speaker;
  } else {
    // Same speaker → continue sentence flow
    conversation += ` ${text}`;
  }
}

conversation = conversation.trim();

return [
  {
    json: {
      cleanedTranscript: conversation
    }
  }
];

// The HTTP node returned an array with one object
// That object contains: { items: [ {...meeting...} ], next_cursor, limit }

const response = $input.first().json;

// Safety checks
if (!response.items || response.items.length === 0) {
  throw new Error("No meetings found in Fathom response");
}

// Get the first meeting
const firstMeeting = response.items[0];

// Return the recording_id so next nodes can use it
return [
  {
    json: {
      recording_id: firstMeeting.recording_id,
      meeting_title: firstMeeting.meeting_title,
      share_url: firstMeeting.share_url
    }
  }
];
